(5, 5, 'category', 'Corporate Social Responsibility\r\nWe believe in giving back and helping out to the community. We are proud of our Corporate Social Responsibility events in which our colleagues match our corporate contributions and provide financial and physical support to those in need.', 3, 3),
(6, 6, 'category', 'Data science is an interdisciplinary field that uses scientific methods, processes, algorithms and systems to extract knowledge and insights from data in various forms, both structured and unstructured, similar to data mining.', 3, 4),
(7, 7, 'category', 'Ecommerce, also known as electronic commerce or internet commerce, refers to the buying and selling of goods or services using the internet, and the transfer of money and data to execute these transactions.', 3, 9),
(8, 8, 'category', 'Engineering is the creative application of science, mathematical methods, and empirical evidence to the innovation, design, construction, operation and maintenance of structures, machines, materials, devices, systems, processes, and organizations for the benefit of humankind.', 3, 9),
(9, 9, 'category', 'insights driven by data', 3, 1),
(10, 10, 'category', 'A market, or marketplace, is a location where people regularly gather for the purchase and sale of provisions, livestock, and other goods.', 3, 3),
(11, 11, 'category', 'Product management is an organisational lifecycle function within a company dealing with the planning, forecasting, and production, or marketing of a product or products at all stages of the product lifecycle. ', 3, 6),
(12, 12, 'category', 'Retail is the process of selling consumer goods or services to customers through multiple channels of distribution to earn a profit.', 3, 5),
(14, 14, 'nav_menu', '', 0, 10);
